// src/components/EditCard.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './EditCard.css';
import { getCreditCardById, getBankCardById, updateCreditCardById, updateBankCardById } from '../api/apiService';

const EditCard = () => {
    const navigate = useNavigate();
    const { cardType, id } = useParams();
    const [card, setCard] = useState(null);
    const [initialCard, setInitialCard] = useState(null); // Orijinal verileri saklamak için
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);

    const fetchCard = async () => {
        console.log('Kart çekme işlemi başlatılıyor:', cardType, id);
        try {
            let response;
            if (cardType === 'credit') {
                response = await getCreditCardById(id);
            } else if (cardType === 'bank') {
                response = await getBankCardById(id);
            } else {
                throw new Error('Geçersiz kart türü');
            }
            setCard(response.data);
            setInitialCard(response.data); // Orijinal verileri sakla
        } catch (err) {
            console.error('Kartı çekerken hata oluştu:', err);
            setError(true);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchCard();
    }, [cardType, id]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setCard((prevCard) => ({
            ...prevCard,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Güncellenecek payload'ı oluştururken, boş bırakılan alanlar için orijinal değeri kullan.
        const updatedCard = { ...card };
        for (const key in updatedCard) {
            // Eğer kullanıcı alanı boş bıraktıysa, initialCard'dan o alanı al
            if (updatedCard[key] === '' || updatedCard[key] === null) {
                updatedCard[key] = initialCard[key];
            }
        }

        try {
            if (cardType === 'credit') {
                await updateCreditCardById(id, updatedCard);
            } else if (cardType === 'bank') {
                await updateBankCardById(id, updatedCard);
            }
            alert('Kart başarıyla güncellendi!');
            navigate('/cards');
        } catch (err) {
            console.error('Kartı güncellerken hata oluştu:', err);
            alert('Kartı güncellerken bir hata oluştu.');
        }
    };

    if (loading) {
        return <p>Yükleniyor...</p>;
    }

    if (error || !card) {
        return <p>Kart bilgisi bulunamadı.</p>;
    }

    return (
        <div className="edit-card-container">
            <h2>Kart Düzenle</h2>
            <form onSubmit={handleSubmit} className="edit-card-form">
                {/* Kart Türünü Gösterme */}
                <div className="form-group">
                    <label>Kart Türü:</label>
                    <span className="card-type-label">
                        {cardType === 'credit' ? 'Kredi Kartı' : 'Banka Kartı'}
                    </span>
                </div>

                {/* Ortak Alanlar */}
                <div className="form-group">
                    <label>Kart Numarası:</label>
                    <input
                        type="text"
                        name="CardNumber"
                        value={card.CardNumber || ''}
                        onChange={handleChange}
                        required
                        maxLength="16"
                        className="form-control"
                        placeholder="16 haneli kart numarası"
                    />
                </div>
                <div className="form-group">
                    <label>Kart Sahibi Adı:</label>
                    <input
                        type="text"
                        name="CardHolderName"
                        value={card.CardHolderName || ''}
                        onChange={handleChange}
                        required
                        className="form-control"
                        placeholder="Kart sahibi adı"
                    />
                </div>
                <div className="form-group">
                    <label>Son Kullanma Tarihi:</label>
                    <input
                        type="date"
                        name="ExpirationDate"
                        value={
                            card.ExpirationDate
                                ? new Date(card.ExpirationDate).toISOString().split('T')[0]
                                : ''
                        }
                        onChange={handleChange}
                        required
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label>CVV:</label>
                    <input
                        type="text"
                        name="CVV"
                        value={card.CVV || ''}
                        onChange={handleChange}
                        required
                        maxLength="4"
                        className="form-control"
                        placeholder="3-4 haneli güvenlik kodu"
                    />
                </div>
                <div className="form-group">
                    <label>Banka Adı:</label>
                    <input
                        type="text"
                        name="BankName"
                        value={card.BankName || ''}
                        onChange={handleChange}
                        required
                        className="form-control"
                        placeholder="Banka adı"
                    />
                </div>

                {/* Kredi Kartı Alanları */}
                {cardType === 'credit' && (
                    <div className="card-fields">
                        <h3>Kredi Kartı Bilgileri</h3>
                        <div className="form-group">
                            <label>Kredi Limiti:</label>
                            <input
                                type="number"
                                name="CreditLimit"
                                value={card.CreditLimit || ''}
                                onChange={handleChange}
                                required
                                className="form-control"
                                placeholder="Örneğin: 5000"
                            />
                        </div>
                        <div className="form-group">
                            <label>Kullanılabilir Bakiye:</label>
                            <input
                                type="number"
                                name="AvailableBalance"
                                value={card.AvailableBalance || ''}
                                onChange={handleChange}
                                required
                                className="form-control"
                                placeholder="Örneğin: 2500"
                            />
                        </div>
                        <div className="form-group">
                            <label>Minimum Ödeme:</label>
                            <input
                                type="number"
                                name="MinimumPayment"
                                value={card.MinimumPayment || ''}
                                onChange={handleChange}
                                required
                                className="form-control"
                                placeholder="Örneğin: 100"
                            />
                        </div>
                        <div className="form-group">
                            <label>Faiz Oranı (%):</label>
                            <input
                                type="number"
                                name="InterestRate"
                                value={card.InterestRate || ''}
                                onChange={handleChange}
                                required
                                step="0.01"
                                className="form-control"
                                placeholder="Örneğin: 1.5"
                            />
                        </div>
                        <div className="form-group">
                            <label>Ekstre Tarihi:</label>
                            <input
                                type="date"
                                name="BillingDate"
                                value={
                                    card.BillingDate
                                        ? new Date(card.BillingDate).toISOString().split('T')[0]
                                        : ''
                                }
                                onChange={handleChange}
                                required
                                className="form-control"
                            />
                        </div>
                        <div className="form-group">
                            <label>Son Ödeme Tarihi:</label>
                            <input
                                type="date"
                                name="DueDate"
                                value={
                                    card.DueDate
                                        ? new Date(card.DueDate).toISOString().split('T')[0]
                                        : ''
                                }
                                onChange={handleChange}
                                required
                                className="form-control"
                            />
                        </div>
                        <div className="form-group form-check">
                            <input
                                type="checkbox"
                                name="Installments"
                                checked={card.Installments || false}
                                onChange={handleChange}
                                className="form-check-input"
                                id="Installments"
                            />
                            <label className="form-check-label" htmlFor="Installments">
                                Taksit Desteği Var mı?
                            </label>
                        </div>
                    </div>
                )}

                {/* Banka Kartı Alanları */}
                {cardType === 'bank' && (
                    <div className="card-fields">
                        <h3>Banka Kartı Bilgileri</h3>
                        <div className="form-group">
                            <label>Hesap Numarası:</label>
                            <input
                                type="text"
                                name="AccountNumber"
                                value={card.AccountNumber || ''}
                                onChange={handleChange}
                                required
                                className="form-control"
                                placeholder="Hesap numarası"
                            />
                        </div>
                        <div className="form-group">
                            <label>IBAN:</label>
                            <input
                                type="text"
                                name="IBAN"
                                value={card.IBAN || ''}
                                onChange={handleChange}
                                required
                                className="form-control"
                                placeholder="IBAN numarası"
                            />
                        </div>
                        <div className="form-group">
                            <label>Bakiye:</label>
                            <input
                                type="number"
                                name="Balance"
                                value={card.Balance || ''}
                                onChange={handleChange}
                                required
                                className="form-control"
                                placeholder="Örneğin: 1500"
                            />
                        </div>
                        <div className="form-group">
                            <label>Günlük Para Çekme Limiti:</label>
                            <input
                                type="number"
                                name="WithdrawalLimit"
                                value={card.WithdrawalLimit || ''}
                                onChange={handleChange}
                                required
                                className="form-control"
                                placeholder="Örneğin: 500"
                            />
                        </div>
                        <div className="form-group form-check">
                            <input
                                type="checkbox"
                                name="IsContactless"
                                checked={card.IsContactless || false}
                                onChange={handleChange}
                                className="form-check-input"
                                id="IsContactless"
                            />
                            <label className="form-check-label" htmlFor="IsContactless">
                                Temassız Ödeme Desteği Var mı?
                            </label>
                        </div>
                    </div>
                )}

                <button type="submit" className="btn btn-primary">
                    Güncelle
                </button>
            </form>
        </div>
    );
};

export default EditCard;
