// contexts/AuthContext.js
import React, { createContext, useContext, useEffect, useState } from 'react';
import { getRolPermissionsById } from '../api/apiService';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {


    // SessionStorage�dan ba�lang�� de�erlerini alabilirsiniz.
    const [auth, setAuth] = useState({
        token: sessionStorage.getItem('token') || null,
        role: sessionStorage.getItem('role') || null,
        permissions: JSON.parse(sessionStorage.getItem('permissions')) || []
    });


    const loginStorage = (token, role, permissions) => {
        sessionStorage.setItem('token', token);
        sessionStorage.setItem('role', role);
        sessionStorage.setItem('permissions', JSON.stringify(permissions));
        setAuth({ token, role, permissions });
    };

    // Logout fonksiyonu: SessionStorage temizlenir ve auth state s�f�rlan�r.
    const logout = () => {
        sessionStorage.clear();
        setAuth({ token: null, role: null, permissions: [] });
    };

    // �zin kontrol� i�in yard�mc� fonksiyon.
    const hasPermission = (controllerName, actionName) => {
        return auth.permissions.some(
            perm =>
                perm.controllerName === controllerName &&
                perm.actionName === actionName
        );
    };

  return (
        <AuthContext.Provider value={{ role, permissions, loading }}>
            {children}
        </AuthContext.Provider>
    );
};
