// src/components/AddCard.js
import React, { useState } from 'react';
import axios from 'axios';
import './AddCard.css'; // Harici CSS dosyasını ekliyoruz

const AddCard = () => {
  const [cardType, setCardType] = useState('');
  const [creditCardData, setCreditCardData] = useState({
    cardNumber: '',
    cardHolderName: '',
    expirationDate: '',
    cvv: '',
    bankName: '',
    creditLimit: '',
    availableBalance: '',
    minimumPayment: '',
    interestRate: '',
    billingDate: '',
    dueDate: '',
    installments: false,
  });

  const [debitCardData, setDebitCardData] = useState({
    cardNumber: '',
    cardHolderName: '',
    expirationDate: '',
    cvv: '',
    bankName: '',
    accountNumber: '',
    IBAN: '',
    balance: '',
    withdrawalLimit: '',
    isContactless: false,
  });

  const handleCardTypeChange = (e) => {
    setCardType(e.target.value);
  };

  const handleCreditCardChange = (e) => {
    const { name, value, type, checked } = e.target;
    setCreditCardData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleDebitCardChange = (e) => {
    const { name, value, type, checked } = e.target;
    setDebitCardData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let payload = {};
    if (cardType === 'Credit') {
      payload = {
        ...creditCardData,
        cardType: 2, // CardType.Credit
      };
    } else if (cardType === 'Debit') {
      payload = {
        ...debitCardData,
        cardType: 1, // CardType.Bank
      };
    } else {
      alert('Lütfen kart türünü seçin.');
      return;
    }

    try {

        let endpoint = '';
        if (cardType === 'Credit') {
          endpoint = 'http://localhost:5283/api/creditcards/create';
        } else if (cardType === 'Debit') {
          endpoint = 'http://localhost:5283/api/bankcards/create';
        } else {
          throw new Error('Geçersiz kart tipi');
        }
      const response = await axios.post(endpoint, payload);
      console.log('Kart başarıyla eklendi:', response.data);
      alert('Kart başarıyla eklendi!');
      // Formu sıfırlamak isterseniz:
      setCardType('');
      setCreditCardData({
        cardNumber: '',
        cardHolderName: '',
        expirationDate: '',
        cvv: '',
        bankName: '',
        creditLimit: '',
        availableBalance: '',
        minimumPayment: '',
        interestRate: '',
        billingDate: '',
        dueDate: '',
        installments: false,
      });
      setDebitCardData({
        cardNumber: '',
        cardHolderName: '',
        expirationDate: '',
        cvv: '',
        bankName: '',
        accountNumber: '',
        IBAN: '',
        balance: '',
        withdrawalLimit: '',
        isContactless: false,
      });
    } catch (error) {
      console.error('Kart ekleme hatası:',  error.response ? error.response.data : error.message);
      alert('Kart eklerken bir hata oluştu.');
    }
  };

  return (
    <div className="add-card-container">
      <h2>Kart Ekle</h2>
      <form onSubmit={handleSubmit} className="add-card-form">
        <div className="form-group">
          <label htmlFor="cardType">Kart Türü:</label>
          <select
            id="cardType"
            value={cardType}
            onChange={handleCardTypeChange}
            required
            className="form-control"
          >
            <option value="">Seçiniz</option>
            <option value="Debit">Banka Kartı</option>
            <option value="Credit">Kredi Kartı</option>
          </select>
        </div>

        {cardType === 'Credit' && (
          <div className="card-fields">
            <h3>Kredi Kartı Bilgileri</h3>
            <div className="form-group">
              <label htmlFor="creditCardNumber">Kart Numarası:</label>
              <input
                type="text"
                id="creditCardNumber"
                name="cardNumber"
                value={creditCardData.cardNumber}
                onChange={handleCreditCardChange}
                required
                maxLength="16"
                className="form-control"
                placeholder="16 haneli kart numarası"
              />
            </div>
            <div className="form-group">
              <label htmlFor="creditCardHolderName">Kart Sahibi Adı:</label>
              <input
                type="text"
                id="creditCardHolderName"
                name="cardHolderName"
                value={creditCardData.cardHolderName}
                onChange={handleCreditCardChange}
                required
                className="form-control"
                placeholder="Kart sahibi adı"
              />
            </div>
            <div className="form-group">
              <label htmlFor="creditExpirationDate">Son Kullanma Tarihi:</label>
              <input
                type="date"
                id="creditExpirationDate"
                name="expirationDate"
                value={creditCardData.expirationDate}
                onChange={handleCreditCardChange}
                required
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label htmlFor="creditCVV">CVV:</label>
              <input
                type="text"
                id="creditCVV"
                name="cvv"
                value={creditCardData.cvv}
                onChange={handleCreditCardChange}
                required
                maxLength="4"
                className="form-control"
                placeholder="3-4 haneli güvenlik kodu"
              />
            </div>
            <div className="form-group">
              <label htmlFor="creditBankName">Banka Adı:</label>
              <input
                type="text"
                id="creditBankName"
                name="bankName"
                value={creditCardData.bankName}
                onChange={handleCreditCardChange}
                required
                className="form-control"
                placeholder="Banka adı"
              />
            </div>
            <div className="form-group">
              <label htmlFor="creditLimit">Kredi Limiti:</label>
              <input
                type="number"
                id="creditLimit"
                name="creditLimit"
                value={creditCardData.creditLimit}
                onChange={handleCreditCardChange}
                required
                className="form-control"
                placeholder="Örneğin: 5000"
              />
            </div>
            <div className="form-group">
              <label htmlFor="availableBalance">Kullanılabilir Bakiye:</label>
              <input
                type="number"
                id="availableBalance"
                name="availableBalance"
                value={creditCardData.availableBalance}
                onChange={handleCreditCardChange}
                required
                className="form-control"
                placeholder="Örneğin: 2500"
              />
            </div>
            <div className="form-group">
              <label htmlFor="minimumPayment">Minimum Ödeme:</label>
              <input
                type="number"
                id="minimumPayment"
                name="minimumPayment"
                value={creditCardData.minimumPayment}
                onChange={handleCreditCardChange}
                required
                className="form-control"
                placeholder="Örneğin: 100"
              />
            </div>
            <div className="form-group">
              <label htmlFor="interestRate">Faiz Oranı (%):</label>
              <input
                type="number"
                id="interestRate"
                name="interestRate"
                value={creditCardData.interestRate}
                onChange={handleCreditCardChange}
                required
                step="0.01"
                className="form-control"
                placeholder="Örneğin: 1.5"
              />
            </div>
            <div className="form-group">
              <label htmlFor="billingDate">Ekstre Tarihi:</label>
              <input
                type="date"
                id="billingDate"
                name="billingDate"
                value={creditCardData.billingDate}
                onChange={handleCreditCardChange}
                required
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label htmlFor="dueDate">Son Ödeme Tarihi:</label>
              <input
                type="date"
                id="dueDate"
                name="dueDate"
                value={creditCardData.dueDate}
                onChange={handleCreditCardChange}
                required
                className="form-control"
              />
            </div>
            <div className="form-group checkbox-group">
              <input
                type="checkbox"
                id="installments"
                name="installments"
                checked={creditCardData.installments}
                onChange={handleCreditCardChange}
                className="form-checkbox"
              />
              <label htmlFor="installments">Taksit Desteği Var mı?</label>
            </div>
          </div>
        )}

        {cardType === 'Debit' && (
          <div className="card-fields">
            <h3>Banka Kartı Bilgileri</h3>
            <div className="form-group">
              <label htmlFor="debitCardNumber">Kart Numarası:</label>
              <input
                type="text"
                id="debitCardNumber"
                name="cardNumber"
                value={debitCardData.cardNumber}
                onChange={handleDebitCardChange}
                required
                maxLength="16"
                className="form-control"
                placeholder="16 haneli kart numarası"
              />
            </div>
            <div className="form-group">
              <label htmlFor="debitCardHolderName">Kart Sahibi Adı:</label>
              <input
                type="text"
                id="debitCardHolderName"
                name="cardHolderName"
                value={debitCardData.cardHolderName}
                onChange={handleDebitCardChange}
                required
                className="form-control"
                placeholder="Kart sahibi adı"
              />
            </div>
            <div className="form-group">
              <label htmlFor="debitExpirationDate">Son Kullanma Tarihi:</label>
              <input
                type="date"
                id="debitExpirationDate"
                name="expirationDate"
                value={debitCardData.expirationDate}
                onChange={handleDebitCardChange}
                required
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label htmlFor="debitCVV">CVV:</label>
              <input
                type="text"
                id="debitCVV"
                name="cvv"
                value={debitCardData.cvv}
                onChange={handleDebitCardChange}
                required
                maxLength="4"
                className="form-control"
                placeholder="3-4 haneli güvenlik kodu"
              />
            </div>
            <div className="form-group">
              <label htmlFor="debitBankName">Banka Adı:</label>
              <input
                type="text"
                id="debitBankName"
                name="bankName"
                value={debitCardData.bankName}
                onChange={handleDebitCardChange}
                required
                className="form-control"
                placeholder="Banka adı"
              />
            </div>
            <div className="form-group">
              <label htmlFor="accountNumber">Hesap Numarası:</label>
              <input
                type="text"
                id="accountNumber"
                name="accountNumber"
                value={debitCardData.accountNumber}
                onChange={handleDebitCardChange}
                required
                className="form-control"
                placeholder="Hesap numarası"
              />
            </div>
            <div className="form-group">
              <label htmlFor="IBAN">IBAN:</label>
              <input
                type="text"
                id="IBAN"
                name="IBAN"
                value={debitCardData.IBAN}
                onChange={handleDebitCardChange}
                required
                className="form-control"
                placeholder="IBAN numarası"
              />
            </div>
            <div className="form-group">
              <label htmlFor="balance">Bakiye:</label>
              <input
                type="number"
                id="balance"
                name="balance"
                value={debitCardData.balance}
                onChange={handleDebitCardChange}
                required
                className="form-control"
                placeholder="Örneğin: 1500"
              />
            </div>
            <div className="form-group">
              <label htmlFor="withdrawalLimit">Günlük Para Çekme Limiti:</label>
              <input
                type="number"
                id="withdrawalLimit"
                name="withdrawalLimit"
                value={debitCardData.withdrawalLimit}
                onChange={handleDebitCardChange}
                required
                className="form-control"
                placeholder="Örneğin: 500"
              />
            </div>
            <div className="form-group checkbox-group">
              <input
                type="checkbox"
                id="isContactless"
                name="isContactless"
                checked={debitCardData.isContactless}
                onChange={handleDebitCardChange}
                className="form-checkbox"
              />
              <label htmlFor="isContactless">Temassız Ödeme Desteği Var mı?</label>
            </div>
          </div>
        )}

        <button type="submit" className="submit-button">
          Kaydet
        </button>
      </form>
    </div>
  );
};

export default AddCard;
