// src/components/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import './Login.css';
import { login as loginApi, getRolPermissionsById } from '../api/apiService';

const Login = () => {
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const { loginStorage, hasPermission } = useContext(AuthContext);


    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await loginApi({ userName, password });
            console.log('Login response:', response);
            console.log('Login response:', response.data);
            console.log('Login response:', response.data.data);


            /*if (!response || response.error || !response.data || !response.data.data) {
                setError(response?.data?.message || 'Giri� ba�ar�s�z: Ge�ersiz yan�t.');
                return;
            }
            */

            const { token, role } = response.data.data;
            if (!token) {
                setError('Token al�namad�.');
                return;
            }

            console.log('Token:', token);
            console.log('Role:', role);

            const permissionsResponse = await getRolPermissionsById(role);
            console.log('Permissions response:', permissionsResponse);
            console.log('Permissions response:', permissionsResponse.data);
            console.log('Permissions response:', permissionsResponse.data.data);

            /*
            if (!permissionsResponse || !permissionsResponse.data || !Array.isArray(permissionsResponse.data.data)) {
                setError(permissionsResponse?.data?.message || 'Yetkisiz giri�: izin bilgilerine ula��lamad�.');
                return;
            }
            */

            const permissions = permissionsResponse.data.data;
            //console.log('Permissions:', permissions);

            if (!permissions.length) {
                setError('Yetkisiz giris: izin bilgileri bos.');
                return;
            }

            loginStorage(token, role, permissions, userName);

            // Kullan�c�n�n "Kartlar� G�r�nt�leme" izni olup olmad���n� kontrol et
            if (!hasPermission('AuthController', 'Login')) {
                alert('Giri� yapmak i�in yetkiniz yok.');
                return;
            }
                navigate('/cards');
            
        } catch (err) {
            console.error('Giris hatasi:', err);
            setError(err.message || 'Kullan�c� ad� veya �ifre hatal�.');
        }
    };

    return (
        <div className="login-container">
            <h2>Giris Yap</h2>
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleLogin} className="login-form">
                <div className="form-group">
                    <label htmlFor="userName">Kullanici Adi</label>
                    <input
                        type="text"
                        id="userName"
                        placeholder="Kullanici Adi"
                        value={userName}
                        onChange={(e) => setUserName(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Sifre</label>
                    <input
                        type="password"
                        id="password"
                        placeholder="Sifre"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="login-button">
                    Giris Yap
                </button>
            </form>
        </div>
    );
};

export default Login;
