// src/components/AddCard.js
import React, { useState } from 'react';
import axios from 'axios';

const AddCard = () => {
  const [cardType, setCardType] = useState('');
  const [creditCardData, setCreditCardData] = useState({
    cardNumber: '',
    cardHolderName: '',
    expirationDate: '',
    cvv: '',
    bankName: '',
    creditLimit: '',
    availableBalance: '',
    minimumPayment: '',
    interestRate: '',
    billingDate: '',
    dueDate: '',
    installments: false,
  });

  const [debitCardData, setDebitCardData] = useState({
    cardNumber: '',
    cardHolderName: '',
    expirationDate: '',
    cvv: '',
    bankName: '',
    accountNumber: '',
    IBAN: '',
    balance: '',
    withdrawalLimit: '',
    isContactless: false,
  });

  const handleCardTypeChange = (e) => {
    setCardType(e.target.value);
  };

  const handleCreditCardChange = (e) => {
    const { name, value, type, checked } = e.target;
    setCreditCardData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleDebitCardChange = (e) => {
    const { name, value, type, checked } = e.target;
    setDebitCardData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let payload = {};
    if (cardType === 'Credit') {
      payload = {
        ...creditCardData,
        cardType: 2, // CardType.Credit
      };
    } else if (cardType === 'Debit') {
      payload = {
        ...debitCardData,
        cardType: 1, // CardType.Bank
      };
    } else {
      alert('Lütfen kart türünü seçin.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/cards', payload);
      console.log('Kart başarıyla eklendi:', response.data);
      alert('Kart başarıyla eklendi!');
      // Formu sıfırlamak isterseniz:
      setCardType('');
      setCreditCardData({
        cardNumber: '',
        cardHolderName: '',
        expirationDate: '',
        cvv: '',
        bankName: '',
        creditLimit: '',
        availableBalance: '',
        minimumPayment: '',
        interestRate: '',
        billingDate: '',
        dueDate: '',
        installments: false,
      });
      setDebitCardData({
        cardNumber: '',
        cardHolderName: '',
        expirationDate: '',
        cvv: '',
        bankName: '',
        accountNumber: '',
        IBAN: '',
        balance: '',
        withdrawalLimit: '',
        isContactless: false,
      });
    } catch (error) {
      console.error('Kart ekleme hatası:', error);
      alert('Kart eklerken bir hata oluştu.');
    }
  };

  return (
    <div style={styles.container}>
      <h2>Kart Ekle</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <label style={styles.label}>
          Kart Türü:
          <select value={cardType} onChange={handleCardTypeChange} style={styles.select}>
            <option value="">Seçiniz</option>
            <option value="Debit">Banka Kartı</option>
            <option value="Credit">Kredi Kartı</option>
          </select>
        </label>

        {cardType === 'Credit' && (
          <div style={styles.cardFields}>
            <label style={styles.label}>
              Kart Numarası:
              <input
                type="text"
                name="cardNumber"
                value={creditCardData.cardNumber}
                onChange={handleCreditCardChange}
                required
                maxLength="16"
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Kart Sahibi Adı:
              <input
                type="text"
                name="cardHolderName"
                value={creditCardData.cardHolderName}
                onChange={handleCreditCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Son Kullanma Tarihi:
              <input
                type="date"
                name="expirationDate"
                value={creditCardData.expirationDate}
                onChange={handleCreditCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              CVV:
              <input
                type="text"
                name="cvv"
                value={creditCardData.cvv}
                onChange={handleCreditCardChange}
                required
                maxLength="4"
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Banka Adı:
              <input
                type="text"
                name="bankName"
                value={creditCardData.bankName}
                onChange={handleCreditCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Kredi Limiti:
              <input
                type="number"
                name="creditLimit"
                value={creditCardData.creditLimit}
                onChange={handleCreditCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Kullanılabilir Bakiye:
              <input
                type="number"
                name="availableBalance"
                value={creditCardData.availableBalance}
                onChange={handleCreditCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Minimum Ödeme:
              <input
                type="number"
                name="minimumPayment"
                value={creditCardData.minimumPayment}
                onChange={handleCreditCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Faiz Oranı:
              <input
                type="number"
                name="interestRate"
                value={creditCardData.interestRate}
                onChange={handleCreditCardChange}
                required
                step="0.01"
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Ekstre Tarihi:
              <input
                type="date"
                name="billingDate"
                value={creditCardData.billingDate}
                onChange={handleCreditCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Son Ödeme Tarihi:
              <input
                type="date"
                name="dueDate"
                value={creditCardData.dueDate}
                onChange={handleCreditCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.checkboxLabel}>
              Taksit Desteği Var mı?
              <input
                type="checkbox"
                name="installments"
                checked={creditCardData.installments}
                onChange={handleCreditCardChange}
                style={styles.checkbox}
              />
            </label>
          </div>
        )}

        {cardType === 'Debit' && (
          <div style={styles.cardFields}>
            <label style={styles.label}>
              Kart Numarası:
              <input
                type="text"
                name="cardNumber"
                value={debitCardData.cardNumber}
                onChange={handleDebitCardChange}
                required
                maxLength="16"
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Kart Sahibi Adı:
              <input
                type="text"
                name="cardHolderName"
                value={debitCardData.cardHolderName}
                onChange={handleDebitCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Son Kullanma Tarihi:
              <input
                type="date"
                name="expirationDate"
                value={debitCardData.expirationDate}
                onChange={handleDebitCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              CVV:
              <input
                type="text"
                name="cvv"
                value={debitCardData.cvv}
                onChange={handleDebitCardChange}
                required
                maxLength="4"
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Banka Adı:
              <input
                type="text"
                name="bankName"
                value={debitCardData.bankName}
                onChange={handleDebitCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Hesap Numarası:
              <input
                type="text"
                name="accountNumber"
                value={debitCardData.accountNumber}
                onChange={handleDebitCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              IBAN:
              <input
                type="text"
                name="IBAN"
                value={debitCardData.IBAN}
                onChange={handleDebitCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Bakiye:
              <input
                type="number"
                name="balance"
                value={debitCardData.balance}
                onChange={handleDebitCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.label}>
              Günlük Para Çekme Limiti:
              <input
                type="number"
                name="withdrawalLimit"
                value={debitCardData.withdrawalLimit}
                onChange={handleDebitCardChange}
                required
                style={styles.input}
              />
            </label>
            <label style={styles.checkboxLabel}>
              Temassız Ödeme Desteği Var mı?
              <input
                type="checkbox"
                name="isContactless"
                checked={debitCardData.isContactless}
                onChange={handleDebitCardChange}
                style={styles.checkbox}
              />
            </label>
          </div>
        )}

        <button type="submit" style={styles.button}>
          Kaydet
        </button>
      </form>
    </div>
  );
};

