  },
  select: {
    padding: '8px',
    marginTop: '5px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  input: {
    padding: '8px',
    marginTop: '5px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  checkboxLabel: {
    marginBottom: '15px',
    display: 'flex',
    alignItems: 'center',
    fontWeight: 'bold',
  },
  checkbox: {
    marginLeft: '10px',
    transform: 'scale(1.5)',
  },
  button: {
    padding: '10px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
