// src/components/AddCard.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AddCard.css';
import endpoints from '../api/apiEndpoint';
import apiClient from '../api/apiClient';

const AddCard = () => {
  const navigate = useNavigate();
    const [cardType, setCardType] = useState('');

  const [creditCardData, setCreditCardData] = useState({
    cardNumber: '',
    cardHolderName: '',
    expirationDate: '',
    cvv: '',
    bankName: '',
    creditLimit: '',
    availableBalance: '',
    minimumPayment: '',
    interestRate: '',
    billingDate: '',
    dueDate: '',
    installments: false,
  });

  const [bankCardData, setBankCardData] = useState({
    cardNumber: '',
    cardHolderName: '',
    expirationDate: '',
    cvv: '',
    bankName: '',
    accountNumber: '',
    IBAN: '',
    balance: '',
    withdrawalLimit: '',
    isContactless: false,
  });

  const handleCardTypeChange = (e) => setCardType(e.target.value);

    const handleInputChange = (e, setState) => {
        const { name, value, type, checked } = e.target;
        setState((prevData) => ({
            ...prevData,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

  const handleSubmit = async (e) => {
    e.preventDefault();

      if (!cardType) {
          alert('Lütfen kart türünü seçin.');
          return;
      }
      const payload = cardType === 'Credit'
          ? { ...creditCardData, cardType: 2 } // 2: Kredi Kartı
          : { ...bankCardData, cardType: 1 }; // 1: Banka Kartı

      const endpoint = cardType === 'Credit'
          ? endpoints.creditcards.create
          : endpoints.bankcards.create;
    try {

      
      const response = await apiClient.post(endpoint, payload);

      console.log('Kart başarıyla eklendi:', response.data);
        alert('Kart başarıyla eklendi!');

      // Formu sıfırlamak isterseniz:
      setCardType('');
      setCreditCardData({
        cardNumber: '',
        cardHolderName: '',
        expirationDate: '',
        cvv: '',
        bankName: '',
        creditLimit: '',
        availableBalance: '',
        minimumPayment: '',
        interestRate: '',
        billingDate: '',
        dueDate: '',
        installments: false,
      });
      setBankCardData({
        cardNumber: '',
        cardHolderName: '',
        expirationDate: '',
        cvv: '',
        bankName: '',
        accountNumber: '',
        IBAN: '',
        balance: '',
        withdrawalLimit: '',
        isContactless: false,
      });
      navigate('/cards'); // Kartlar listesine yönlendirme
    } catch (error) {
      console.error('Kart ekleme hatası:',  error.response ? error.response.data : error.message);
      alert('Kart eklerken bir hata oluştu.');
    }
  };

    return (
        <div className="add-card-container">
            <h2>Kart Ekle</h2>
            <form onSubmit={handleSubmit} className="add-card-form">
                <div className="form-group">
                    <label>Kart Türü:</label>
                    <select value={cardType} onChange={handleCardTypeChange} required className="form-control">
                        <option value="">Seçiniz</option>
                        <option value="Bank">Banka Kartı</option>
                        <option value="Credit">Kredi Kartı</option>
                    </select>
                </div>

                {cardType && (
                    <div className="card-fields">
                        <h3>{cardType === 'Credit' ? 'Kredi Kartı Bilgileri' : 'Banka Kartı Bilgileri'}</h3>
                        {Object.keys(cardType === 'Credit' ? creditCardData : bankCardData).map((key) => (
                            <div key={key} className="form-group">
                                <label>
                                    {key.replace(/([A-Z])/g, ' $1').trim()}:
                                    {typeof (cardType === 'Credit' ? creditCardData[key] : bankCardData[key]) === 'boolean' ? (
                                        <input
                                            type="checkbox"
                                            name={key}
                                            checked={cardType === 'Credit' ? creditCardData[key] : bankCardData[key]}
                                            onChange={(e) => handleInputChange(e, cardType === 'Credit' ? setCreditCardData : setBankCardData)}
                                            className="form-check-input"
                                        />
                                    ) : (
                                        <input
                                            type={key.includes('Date') ? 'date' : key.includes('cvv') || key.includes('cardNumber') ? 'text' : 'number'}
                                            name={key}
                                            value={cardType === 'Credit' ? creditCardData[key] : bankCardData[key]}
                                            onChange={(e) => handleInputChange(e, cardType === 'Credit' ? setCreditCardData : setBankCardData)}
                                            required
                                            className="form-control"
                                            placeholder={key.includes('cvv') ? '3-4 haneli güvenlik kodu' : ''}
                                        />
                                    )}
                                </label>
                            </div>
                        ))}
                    </div>
                )}

                <button type="submit" className="btn btn-primary">Kaydet</button>
            </form>
        </div>
    );
};

export default AddCard;
